-- ----------------------------
-- Table structure for mr_article
-- ----------------------------
DROP TABLE IF EXISTS `mr_article`;
CREATE TABLE `mr_article` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '文章id',
  `title` varchar(80) NOT NULL COMMENT '标题',
  `content` text NOT NULL COMMENT '内容',
  `create_time` int(10) NOT NULL COMMENT '发布时间',
  `create_ip` bigint(20) NOT NULL COMMENT '操作ip',
  `kind` int(10) NOT NULL COMMENT '分类',
  `view` int(10) NOT NULL COMMENT '阅读量',
  `status` tinyint(4) NOT NULL COMMENT '状态',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=26 DEFAULT CHARSET=utf8;

