INSERT INTO `mr_article` VALUES ('1', 'MR.WLT今日起正式启用！', '&lt;p&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;忙里偷闲给自己写了一个小博客，嘿嘿。。。算起来我接触PHP这门语言也有小半年了吧，学到了很多。在此还要感谢BIGCAN，&lt;img src=&quot;http://linteng.wang/Public/kindeditor/plugins/emoticons/images/13.gif&quot; border=&quot;0&quot; alt=&quot;&quot; /&gt;一个让我不断成长的地方。感谢大家的帮助！感谢孟老师给我这个学习的机会。第一次给自己写了一个小网站，感觉还是不错的&lt;img src=&quot;http://linteng.wang/Public/kindeditor/plugins/emoticons/images/13.gif&quot; border=&quot;0&quot; alt=&quot;&quot; /&gt;。。。程序猿加油！！！\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;img src=&quot;/Uploads/image/20150424/20150424232851_93836.jpg&quot; alt=&quot;&quot; /&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;', '1429889341', '992894353', '4', '52', '1');
INSERT INTO `mr_article` VALUES ('2', '微信通过授权获取用户信息', '<p>\r\n	<!--?php<br /--> namespace Test\\Controller;<br />\r\n<br />\r\nuse Think\\Controller;<br />\r\n<br />\r\n/**<br />\r\n* 微信权限类控制器<br />\r\n* @author MR.WLT<br />\r\n*/<br />\r\nclass BaseController extends Controller<br />\r\n{ <br />\r\n//进入授权引导<br />\r\npublic function index(){<br />\r\n$openid = cookie(\'openid\');<br />\r\nif($openid){<br />\r\n$this-&gt;redirect(\'\');<br />\r\n}else{<br />\r\n$appid = \'\';//微信平台的appid<br />\r\n$redirect_uri = urlencode(\'\');//回调链接<br />\r\n$url = \'https://open.weixin.qq.com/connect/oauth2/authorize?appid=\'.$appid.\'&amp;redirect_uri=\'.$redirect_uri.\'&amp;response_type=code&amp;scope=snsapi_userinfo\r\n</p>\r\n<p>\r\n	#wechat_redirect\';//授权链接<br />\r\nheader(\"location:$url\");//跳转链接<br />\r\n}<br />\r\n}<br />\r\n//获取access_token<br />\r\npublic function get($code = null){<br />\r\n$appid = \'\';//微信平台的appid<br />\r\n$appsecret = \'\';//微信平台的应用秘钥<br />\r\n$url = \'https://api.weixin.qq.com/sns/oauth2/access_token?appid=\'.$appid.\'&amp;secret=\'.$appsecret.\'&amp;code=\'.$code.\'&amp;grant_type=authorization_code\';//获取access_token 链接<br />\r\n$data_json = file_get_contents($url);//请求返回数据<br />\r\n$data = json_decode($data_json,true);//json=&gt;数组//true时为数组否则为数组<br />\r\n$access_token = $data[\'access_token\'];//access_token 值<br />\r\n$openid = $data[\'openid\'];//openid 值<br />\r\n$this-&gt;info($access_token,$openid);//提交给info方法获取用户信息<br />\r\n}<br />\r\n//拉取用户信息<br />\r\npublic function info($access_token = null,$openid = null){<br />\r\n$url = \'https://api.weixin.qq.com/sns/userinfo?access_token=\'.$access_token.\'&amp;openid=\'.$openid.\'&amp;lang=zh_CN\';//获取用户信息的url<br />\r\n$data_json = file_get_contents($url);//请求返回数据<br />\r\n$data = json_decode($data_json,true);//json=&gt;数组<br />\r\ncookie(\'openid\',$data[\'openid\']);<br />\r\n$this-&gt;redirect(\'\');<br />\r\n}<br />\r\n}<br />\r\n<a href=\"/Uploads/file/20150425/20150425094444_13708.zip\" target=\"_blank\">微信获取用户基本信息类</a> \r\n</p>', '1429926353', '992894353', '1', '78', '1');
INSERT INTO `mr_article` VALUES ('10', '解码base64获取图片', '&nbsp; &nbsp; /**<br />\r\n&nbsp; &nbsp; &nbsp;* 解码base64获取图片<br />\r\n&nbsp; &nbsp; &nbsp;* @author MR.WLT<br />\r\n&nbsp; &nbsp; &nbsp;*/<br />\r\n&nbsp; &nbsp; public function base64($pic = null){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; //保存base64字符串为图片<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; //匹配出图片的格式<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; if (preg_match(\'/^(data:\\s*image\\/(\\w+);base64,)/\', $pic, $result)){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $type = $result[2];<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //创建文件夹<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $path=date(\"Y-m-d\"); &nbsp;//获取当前时间<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $path = \'Uploads/Header/\'.$path;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //检测变量中的文件夹是否存在<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if(!file_exists($path)){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; mkdir($path,0777);//创建文件夹<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //创建文件夹end<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //解码成文件存到服务器<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $new_file = \"{$path}\".\"/\".NOW_TIME.\".\".\"{$type}\";<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if (file_put_contents($new_file, base64_decode(str_replace($result[1], \'\', $pic)))){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //写入picture表<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $picture = array(<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; \'type\' =&gt; \'header\',<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; \'path\' =&gt; \'/\'.$new_file,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; \'status\'=&gt;1,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; \'create_time\'=&gt; NOW_TIME,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; );<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $return = M(\'Picture\')-&gt;data($picture)-&gt;add();<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //判断是否写入数据库中<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if($return){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //修改通讯录照片<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $data = array(\'cover_id\'=&gt;$return);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $return_1 = M(\'Address\')-&gt;where(array(\'username\'=&gt;session(\'username\')))-&gt;data($data)-&gt;save();<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if($return_1){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $data = array (\"Data\" =&gt; true);//修改成功<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }else{<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $data = array (\"Data\" =&gt; false);//修改失败<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; //修改通讯录照片end<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }else{<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $data = array (\"Data\" =&gt; false);//写入失败<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }else{<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $data = array (\"Data\" =&gt; false);//解码保存失败<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; echo json_encode($data);<br />\r\n&nbsp; &nbsp; }<br />', '1430439945', '3396345482', '1', '27', '1');
INSERT INTO `mr_article` VALUES ('7', 'Eclipse中安装genymotion插件方法', '安装Eclipse插件，可以选择在线安装。<br />\r\n<br />\r\n启动Eclipse，Help-&gt;Install New Software...-&gt;Add<br />\r\n<br />\r\n填写一下信息：<br />\r\n<br />\r\nLocation: http://plugins.genymotion.com/eclipse。<br />\r\n<br />\r\n<p>\r\n	安装完毕之后，点击Eclipse上的新出现的genymotion图标即可启用genymotion调试。\r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/image/20150425/20150425164845_90108.png\" alt=\"\" /> \r\n</p>', '1429951537', '992894353', '9', '40', '1');
INSERT INTO `mr_article` VALUES ('8', 'Volley框架的使用', '<br />\r\n<p>\r\n	<strong>所谓Volley,它是2013年Google I/O上发布的一款网络框架，基于Android平台，能使网络通信更快，更简单，更健全。</strong> \r\n</p>\r\n<p>\r\n	<strong>它的优点：（1）默认Android2.3及以上基于HttpURLConnection，2.3以下使用基于HttpClient；</strong> \r\n</p>\r\n<p>\r\n	<strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>（2）符合Http <strong>缓存语义</strong> 的缓存机制（提供了默认的磁盘和内存等缓存）；</strong> \r\n</p>\r\n<p>\r\n	<strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; （3）请求队列的优先级排序；</strong> \r\n</p>\r\n<p>\r\n	<strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; （4）提供多样的取消机制；</strong> \r\n</p>\r\n<p>\r\n	<strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; （5）提供简便的图片加载工具（其实图片的加载才是我们最为看重的功能）；</strong> \r\n</p>\r\n<p>\r\n	<strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; （6）<span style=\"color:#ff0000;\">一个优秀的框架<span style=\"color:#000000;\">。</span></span></strong> \r\n</p>\r\n<p>\r\n	<strong>不足之处也有：它只适合<strong>数据量小，通信频繁</strong>的网络操作，如果是数据量大的，像音频，视频等的传输，还是不要使用Volley的为好。</strong> \r\n</p>\r\n<p>\r\n	<strong><img src=\"http://images.cnitblog.com/blog/653760/201412/301027181066871.png\" alt=\"\" /></strong> \r\n</p>', '1430145650', '992894353', '9', '41', '1');
INSERT INTO `mr_article` VALUES ('3', 'WAMP环境部署', '1.安装wamp软件<br />\r\n<br />\r\n2.mysql控制台修改mysql密码<br />\r\n修改方式：<br />\r\n1.输入 use mysql<br />\r\n2.输入 update user set password =PASSWORD(\'密码\') where user=\'root\';//注意大小写和分号<br />\r\n3.输入 flush privileges<br />\r\n4.输入 quit<br />\r\n<br />\r\n3.修改phpmyadmin的登陆方式：config.inc.php中 $cfg[\'Servers\'][$i][\'auth_type\']=\'http\'<br />\r\n<br />\r\n4.配置rewrite规则:<br />\r\n1.httpd.conf配置文件中加载了mod_rewrite.so模块 <br />\r\n2.AllowOverride None 将None改为 All <br />\r\n3.把下面的内容保存为.htaccess文件放到应用入口文件的同级目录下 <br />\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	/*******.htaccess文件***********/\r\n</p>\r\n<ifmodule mod_rewrite.c=\"\">RewriteEngine on<br />\r\nRewriteCond %{REQUEST_FILENAME} !-d<br />\r\nRewriteCond %{REQUEST_FILENAME} !-f<br />\r\nRewriteRule ^(.*)$ index.php/$1 [QSA,PT,L]<br />\r\n<br />\r\n<a class=\"ke-insertfile\" href=\"/Uploads/file/20150425/20150425094834_89479.txt\" target=\"_blank\">WAMP环境部署</a><br />\r\n<br />', '1429926533', '992894353', '3', '48', '1');
INSERT INTO `mr_article` VALUES ('9', '解决thinkphp插入html转义问题', '<p>\r\n	ThinkPHP的conf文件中的Convention.php有一个配置选项\r\n</p>\r\n\'DEFAULT_FILTER\'        =&gt;  \'htmlspecialchars\', // 默认参数过滤方法 用于I函数...\r\n<p>\r\n	默认这个方法是开启的。也就是说，我们往数据库里面存储的数据中都会经过htmlspecialchars这个函数的转义处理。\r\n</p>\r\n<p>\r\n	将此行代码注释后，再出现转义情况。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '1430235920', '3396345482', '1', '35', '1');
INSERT INTO `mr_article` VALUES ('4', 'CSS限制标题长度', '<p>\r\n	.ellipsis{text-overflow: ellipsis; overflow: hidden;&nbsp; word-break: break-all; white-space: nowrap;}\r\n</p>\r\n<p>\r\n	<a class=\"ke-insertfile\" href=\"/Uploads/file/20150425/20150425095113_12009.txt\" target=\"_blank\">css限制标题长度</a> \r\n</p>', '1429926693', '992894353', '3', '49', '1');
INSERT INTO `mr_article` VALUES ('5', 'GBK=&gt;UTF-8', '&lt;a class=&quot;ke-insertfile&quot; href=&quot;/Uploads/file/20150425/20150425095422_93453.rar&quot; target=&quot;_blank&quot;&gt;GBK=&amp;gt;UTF-8&lt;/a&gt;', '1429926893', '992894353', '3', '68', '1');
INSERT INTO `mr_article` VALUES ('11', 'java判断两个字符串是否相等', '<p>\r\n	response.equals(\"success\")\r\n</p>\r\n<p>\r\n	java判断两个字符串是否相等\r\n</p>\r\n<p>\r\n	即：A.<span style=\"line-height:14.3999996185303px;\">equals(B)</span>\r\n</p>', '1431082091', '3396345482', '2', '35', '1');
INSERT INTO `mr_article` VALUES ('12', 'Android通知栏实现', '&nbsp;&nbsp;&nbsp; // 通知栏<br />\r\n&nbsp;&nbsp; &nbsp;public void Notice(String response) {<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;String alert = null;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;String alertSuccess = \"数据传输成功，谢谢合作！\";<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;String alertError = \"数据传输失败，尝试重新连接！\";<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if (response.equals(\"success\")) {<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;alert = alertSuccess;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;} else {<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;alert = alertError;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;NotificationManager nm = (NotificationManager) getSystemService(Context.NOTIFICATION_SERVICE);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;Builder mBuilder = new Builder(this);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;// 系统收到通知时，通知栏上面显示的文字。<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;mBuilder.setTicker(alert);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;// 显示在通知栏上的小图标<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;mBuilder.setSmallIcon(R.drawable.ic_launcher);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;// 通知标题<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;mBuilder.setContentTitle(\"MR.WLT\");<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;// 通知内容<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;mBuilder.setContentText(alert);<br />\r\n<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;// 设置大图标，即通知条上左侧的图片（如果只设置了小图标，则此处会显示小图标）<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;mBuilder.setLargeIcon(BitmapFactory.decodeResource(getResources(),<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;R.drawable.ic_launcher));<br />\r\n<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;// 显示在小图标左侧的数字<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;// mBuilder.setNumber(6);<br />\r\n<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;// 设置为不可清除模式<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;mBuilder.setOngoing(false);<br />\r\n<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;// 不设置这个[响铃,闪灯,震动]都不会执行,而是安静.<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;mBuilder.setDefaults(Notification.DEFAULT_LIGHTS<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;| Notification.DEFAULT_SOUND | Notification.DEFAULT_VIBRATE<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;| Notification.DEFAULT_ALL);<br />\r\n<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;// 显示通知，id必须不重复，否则新的通知会覆盖旧的通知（利用这一特性，可以对通知进行更新）<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;nm.notify(100, mBuilder.build());<br />\r\n&nbsp;&nbsp; &nbsp;}<br />\r\n<br />', '1431084792', '3396345482', '9', '31', '1');
INSERT INTO `mr_article` VALUES ('13', 'Android异步网络请求', '&nbsp;&nbsp;&nbsp; // 异步网络请求<br />\r\npublic class HttpTask extends AsyncTask<string, void,=\"\" string=\"\"> {<br />\r\n// 任务执行前操作<br />\r\n@Override<br />\r\nprotected void onPreExecute() {<br />\r\n// TODO Auto-generated method stub<br />\r\nsuper.onPreExecute();<br />\r\n}<br />\r\n<br />\r\n// 完成耗时操作<br />\r\n@Override<br />\r\nprotected String doInBackground(String... params) {<br />\r\n// TODO Auto-generated method stub<br />\r\ntry {<br />\r\nrequestStringData();<br />\r\n} catch (Exception e) {<br />\r\n// TODO: handle exception<br />\r\ne.printStackTrace();<br />\r\n}<br />\r\nreturn null;<br />\r\n}<br />\r\n<br />\r\n// 更新UI操作<br />\r\n@Override<br />\r\nprotected void onPostExecute(String result) {<br />\r\n// TODO Auto-generated method stub<br />\r\nsuper.onPostExecute(result);<br />\r\n}<br />\r\n<br />\r\n}<br />\r\n<br />\r\n</string,>', '1431084851', '3396345482', '9', '31', '1');
INSERT INTO `mr_article` VALUES ('14', 'Apache配置多IP', '<p>\r\n	简单的说,打开httpd.conf 在最后加入如下内容:\r\n</p>\r\n<p>\r\n	<virtualhost 127.0.0.2:80=\"\">\r\n</p>\r\n<virtualhost 127.0.0.2:80=\"\"><img src=\"/Uploads/image/20150511/20150511232114_71782.png\" alt=\"\" /><br />\r\n<p>\r\n	\"d:/AppServ/www2\" 为你的站点存放目录:重启apache2以后，你的虚拟主机就配置好了，以后就可以通过127.0.0.2,和127.0.0.3进入不同的站点了。\r\n</p>', '1431357097', '3396345482', '3', '51', '1');
INSERT INTO `mr_article` VALUES ('17', 'IPV6+谷歌GAE上网', '<div>\r\n	<p>\r\n		IPV6+谷歌GAE上网工具\r\n	</p>\r\n	<p>\r\n		下载地址：http://pan.baidu.com/s/1bn97I39\r\n	</p>\r\n	<p>\r\n		说明：需要google账号，使用时修改局域网代理服务器。\r\n	</p>\r\n	<p>\r\n		<img title=\"\" src=\"/Uploads/image/20150517/20150517161419_64704.png\" alt=\"\" align=\"\" height=\"266\" width=\"300\" />\r\n	</p>\r\n</div>', '1431850296', '3396345482', '3', '23', '1');
INSERT INTO `mr_article` VALUES ('15', '一服务多网站', '<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14.0pt;font-family:隶书;color:#5B9BD5;\">Wamp</span><span style=\"font-size:14.0pt;font-family:隶书;color:#5B9BD5;\">：一个服务多网站的方法</span><span><br />\r\n1</span><span style=\"font-family:宋体;\">、首先先确定的</span><span>wamp</span><span style=\"font-family:宋体;\">服务器已经建好。例如目录在</span><span>D:\\wamp\\www\\</span><span style=\"font-family:宋体;\">里面</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span>2</span><span style=\"font-family:宋体;\">、开启</span><span>httpd-vhosts</span><span style=\"font-family:宋体;\">虚拟主机模块。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span> 1</span><span style=\"font-family:宋体;\">、打开</span><span>apache</span><span style=\"font-family:宋体;\">目录下的</span><span>httpd.conf</span><span style=\"font-family:宋体;\">文件；或在此路径下查找：</span><span>D:\\wamp\\bin\\apache\\Apache2.2.17\\conf</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span>ctrl+f</span><span style=\"font-family:宋体;\">查找</span><span>vhosts,</span><span style=\"font-family:宋体;\">找到如下模块：</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span>#Include conf/extra/httpd-vhosts.conf</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-family:宋体;\">去掉前面的</span><span>#</span></b><b><span style=\"font-family:宋体;\">，这样就开启了</span><span>httpd-vhosts</span></b><b><span style=\"font-family:宋体;\">虚拟主机模块；</span></b><span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span> </span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span>3</span><span style=\"font-family:宋体;\">、打开此路径</span><span>D:\\wamp\\bin\\apache\\apache2.2.22\\conf\\extra</span><span style=\"font-family:宋体;\">，用记事本打开</span><span>httpd-vhosts.conf</span><span style=\"font-family:宋体;\">文件。修改对应项目</span> \r\n</p>\r\n<img src=\"/Uploads/image/20150511/20150511231939_21943.png\" alt=\"\" /><br />\r\n<p class=\"MsoNormal\">\r\n	<span><span></span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span></span> \r\n</p>', '1431357185', '3396345482', '3', '24', '1');
INSERT INTO `mr_article` VALUES ('18', 'W3school离线电子书', '<p>\r\n	网盘地址：http://pan.baidu.com/s/1gdInbqz\r\n</p>\r\n<p>\r\n	<img title=\"\" src=\"/Uploads/image/20150517/20150517201008_18835.png\" alt=\"\" align=\"\" height=\"329\" width=\"600\" /> \r\n</p>', '1431864476', '3396345482', '3', '7', '1');
INSERT INTO `mr_article` VALUES ('19', 'ThinkPHP3.2.2完全开发手册', '<p>\r\n	ThinkPHP3.2.2完全开发手册下载地址：http://pan.baidu.com/s/1eQGTkHW\r\n</p>\r\n<p>\r\n	<img title=\"\" src=\"/Uploads/image/20150517/20150517201442_61048.png\" alt=\"\" align=\"\" height=\"490\" width=\"600\" />\r\n</p>', '1431864894', '3396345482', '1', '10', '1');
INSERT INTO `mr_article` VALUES ('20', '批处理控制是否使用IE代理', '@echo off <br />\r\necho [MR.WLT] please choose close or open<br />\r\nset /p value=<br />\r\nif \"%value%\"==\"close\"&nbsp;&nbsp; (<br />\r\n&nbsp;&nbsp; &nbsp;echo Closing Agent <br />\r\n&nbsp;&nbsp; &nbsp;reg add \"HKCU\\Software\\Microsoft\\Windows\\CurrentVersion\\Internet Settings\" /v ProxyEnable /t REG_DWORD /d 0 /f <br />\r\n&nbsp;&nbsp; &nbsp;echo Finish Close<br />\r\n&nbsp;&nbsp; &nbsp;pause&gt;nul<br />\r\n) else (<br />\r\n&nbsp;&nbsp; &nbsp;echo Opening Agent <br />\r\n&nbsp;&nbsp; &nbsp;reg add \"HKCU\\Software\\Microsoft\\Windows\\CurrentVersion\\Internet Settings\" /v ProxyEnable /t REG_DWORD /d 1 /f <br />\r\n&nbsp;&nbsp; &nbsp;echo Finish Open<br />\r\n&nbsp;&nbsp; &nbsp;pause&gt;nul<br />\r\n)<br />\r\n<br />', '1433483067', '3396345482', '3', '12', '1');
INSERT INTO `mr_article` VALUES ('21', '检测身份证是否合法', '<div class=\"line number1 index0 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	/**\r\n</div>\r\n<div class=\"line number2 index1 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;* 验证身份证号\r\n</div>\r\n<div class=\"line number3 index2 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;* @param $vStr\r\n</div>\r\n<div class=\"line number4 index3 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;* @return bool\r\n</div>\r\n<div class=\"line number5 index4 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;*/\r\n</div>\r\n<div class=\"line number6 index5 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	functionisCreditNo($vStr)\r\n</div>\r\n<div class=\"line number7 index6 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	{\r\n</div>\r\n<div class=\"line number8 index7 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;$vCity=array(\r\n</div>\r\n<div class=\"line number9 index8 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'11\',\'12\',\'13\',\'14\',\'15\',\'21\',\'22\',\r\n</div>\r\n<div class=\"line number10 index9 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'23\',\'31\',\'32\',\'33\',\'34\',\'35\',\'36\',\r\n</div>\r\n<div class=\"line number11 index10 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'37\',\'41\',\'42\',\'43\',\'44\',\'45\',\'46\',\r\n</div>\r\n<div class=\"line number12 index11 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'50\',\'51\',\'52\',\'53\',\'54\',\'61\',\'62\',\r\n</div>\r\n<div class=\"line number13 index12 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'63\',\'64\',\'65\',\'71\',\'81\',\'82\',\'91\'\r\n</div>\r\n<div class=\"line number14 index13 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;);\r\n</div>\r\n<div class=\"line number15 index14 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;\r\n</div>\r\n<div class=\"line number16 index15 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;if(!preg_match(\'/^([\\d]{17}[xX\\d]|[\\d]{15})$/\',$vStr))returnfalse;\r\n</div>\r\n<div class=\"line number17 index16 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;\r\n</div>\r\n<div class=\"line number18 index17 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;if(!in_array(substr($vStr, 0, 2),$vCity))returnfalse;\r\n</div>\r\n<div class=\"line number19 index18 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;\r\n</div>\r\n<div class=\"line number20 index19 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;$vStr= preg_replace(\'/[xX]$/i\',\'a\',$vStr);\r\n</div>\r\n<div class=\"line number21 index20 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;$vLength=strlen($vStr);\r\n</div>\r\n<div class=\"line number22 index21 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;\r\n</div>\r\n<div class=\"line number23 index22 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;if($vLength== 18)\r\n</div>\r\n<div class=\"line number24 index23 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;{\r\n</div>\r\n<div class=\"line number25 index24 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$vBirthday=substr($vStr, 6, 4) .\'-\'.substr($vStr, 10, 2) .\'-\'.substr($vStr, 12, 2);\r\n</div>\r\n<div class=\"line number26 index25 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;}else{\r\n</div>\r\n<div class=\"line number27 index26 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$vBirthday=\'19\'.substr($vStr, 6, 2) .\'-\'.substr($vStr, 8, 2) .\'-\'.substr($vStr, 10, 2);\r\n</div>\r\n<div class=\"line number28 index27 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;}\r\n</div>\r\n<div class=\"line number29 index28 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;\r\n</div>\r\n<div class=\"line number30 index29 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;if(date(\'Y-m-d\',strtotime($vBirthday)) !=$vBirthday)returnfalse;\r\n</div>\r\n<div class=\"line number31 index30 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;if($vLength== 18)\r\n</div>\r\n<div class=\"line number32 index31 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;{\r\n</div>\r\n<div class=\"line number33 index32 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$vSum= 0;\r\n</div>\r\n<div class=\"line number34 index33 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;\r\n</div>\r\n<div class=\"line number35 index34 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for($i= 17 ;$i&gt;= 0 ;$i--)\r\n</div>\r\n<div class=\"line number36 index35 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{\r\n</div>\r\n<div class=\"line number37 index36 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$vSubStr=substr($vStr, 17 -$i, 1);\r\n</div>\r\n<div class=\"line number38 index37 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$vSum+= (pow(2,$i) % 11) * (($vSubStr==\'a\') ? 10 :intval($vSubStr, 11));\r\n</div>\r\n<div class=\"line number39 index38 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n</div>\r\n<div class=\"line number40 index39 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;\r\n</div>\r\n<div class=\"line number41 index40 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if($vSum% 11 != 1)returnfalse;\r\n</div>\r\n<div class=\"line number42 index41 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;}\r\n</div>\r\n<div class=\"line number43 index42 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;\r\n</div>\r\n<div class=\"line number44 index43 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;returntrue;\r\n</div>\r\n<div class=\"line number45 index44 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333330154419px;padding:0px 1em !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;background:none white !important;\">\r\n	}\r\n</div>', '1434875269', '2946570438', '1', '8', '1');
INSERT INTO `mr_article` VALUES ('25', 'IIS环境下安装PHP', '<h2 class=\"title\">\r\n	Microsoft IIS 7.0 及更高版本<a class=\"genanchor\" href=\"http://php.net/manual/zh/install.windows.iis7.php#install.windows.iis7\"> <br />\r\n</a> \r\n</h2>\r\n<p class=\"para\">\r\n	本章包含有在 Windows Vista SP1，Windows 7，Windows Server 2008 以及\r\n  Windows Server 2008 R2下的 IIS 7.0 以及更高版本中手工安装 PHP\r\n  的指南。有关在 Windows XP 和 Windows Server 2003 \r\n  下的 IIS 5.1 和 IIS 6.0 中安装 PHP 的指南见 <a href=\"http://php.net/manual/zh/install.windows.iis6.php\" class=\"link\">Microsoft IIS 5.1 和 IIS 6.0</a> 一章。\r\n</p>\r\n<div class=\"sect2\" id=\"install.windows.iis7.fastcgi.enable\">\r\n	<h3 class=\"title\">\r\n		在 IIS 中激活 FastCGI 支持<a class=\"genanchor\" href=\"http://php.net/manual/zh/install.windows.iis7.php#install.windows.iis7.fastcgi.enable\"> <br />\r\n</a> \r\n	</h3>\r\n	<p class=\"para\">\r\n		默认安装的 IIS 中 FastCGI 模块被关闭。要激活其的步骤在不同版本的 Windows 下不同。\r\n	</p>\r\n	<p class=\"para\">\r\n		要在 Windows Vista SP1 和 Windows 7 中激活 FastCGI 支持：\r\n	</p>\r\n	<div class=\"procedure\">\r\n		<ol>\r\n			<li>\r\n				<p class=\"simpara\">\r\n					在 Windows 开始菜单中选择“运行...”（或在搜索框内），输入“optionalfeatures.exe”并按“确定”（或敲回车键）；\r\n				</p>\r\n			</li>\r\n			<li>\r\n				<p class=\"simpara\">\r\n					在“Windows 功能”对话框中展开“Internet\r\n      信息服务”，“万维网服务”，“应用程序开发功能”，并选中“CGI”的选择框；\r\n				</p>\r\n			</li>\r\n			<li>\r\n				<p class=\"simpara\">\r\n					点击确定按钮并等待安装完成。\r\n				</p>\r\n			</li>\r\n		</ol>\r\n	</div>\r\n	<div class=\"mediaobject\">\r\n		<div class=\"imageobject\">\r\n			<img src=\"http://php.net/manual/zh/images/b4cf2bb34e3c20eebcf8f9e8e7949efd-iis7vistacgi.png\" alt=\"在 Windows Vista SP1 和 Windows 7 中激活 FastCGI 支持\" height=\"375\" width=\"429\" /> \r\n		</div>\r\n	</div>\r\n	<p class=\"para\">\r\n		要在 Windows Server 2008 和 Windows Server 2008 R2 中激活 FastCGI 支持：\r\n	</p>\r\n	<div class=\"procedure\">\r\n		<ol>\r\n			<li>\r\n				<p class=\"simpara\">\r\n					在 Windows 开始菜单中选择 \"Run:\"，输入 \"CompMgmtLauncher\"\r\n      并点击 \"Ok\"；\r\n				</p>\r\n			</li>\r\n			<li>\r\n				<p class=\"simpara\">\r\n					如果在 \"Roles\" 节点下没有 \"Web Server (IIS)\" role，则点击\r\n      \"Add Roles\" 添加之；\r\n				</p>\r\n			</li>\r\n			<li>\r\n				<p class=\"simpara\">\r\n					如果存在 \"Web Server (IIS)\" role，则点击 \"Add Role Services\" 并激活\r\n      \"Application Development\" 组之下的 \"CGI\" 选择框；\r\n				</p>\r\n			</li>\r\n			<li>\r\n				<p class=\"simpara\">\r\n					点击 \"Next\" 及 \"Install\"，等待安装完成。\r\n				</p>\r\n			</li>\r\n		</ol>\r\n	</div>\r\n	<div class=\"mediaobject\">\r\n		<div class=\"imageobject\">\r\n			<img src=\"http://php.net/manual/zh/images/b4cf2bb34e3c20eebcf8f9e8e7949efd-iis7w2k8cgi.png\" alt=\"在 Windows Server 2008 和 Windows Server 2008 R2 中激活 FastCGI 支持\" height=\"411\" width=\"546\" /> \r\n		</div>\r\n	</div>\r\n</div>\r\n<div class=\"sect2\" id=\"install.windows.iis7.fastcgi.conf\">\r\n	<h3 class=\"title\">\r\n		配置 IIS 以处理 PHP 请求<a class=\"genanchor\" href=\"http://php.net/manual/zh/install.windows.iis7.php#install.windows.iis7.fastcgi.conf\"> <br />\r\n</a> \r\n	</h3>\r\n	<p class=\"para\">\r\n		根据<a href=\"http://php.net/manual/zh/install.windows.manual.php\" class=\"link\">手工安装步骤</a>的说明下载和安装 PHP。\r\n	</p>\r\n	<blockquote class=\"note\">\r\n		<p>\r\n			<strong class=\"note\">Note</strong>:\r\n		</p>\r\n		<p class=\"para\">\r\n			在使用 IIS 时推荐使用非线程安全的 PHP。可以在 <a href=\"http://windows.php.net/download/\" class=\"link external\">»&nbsp;PHP for Windows: \r\n      Binaries and Sources Releases</a> 下载。\r\n		</p>\r\n	</blockquote>\r\n	<p class=\"para\">\r\n		按以下示例在php.ini文件中配置\r\n   针对 CGI- 和 FastCGI- 的指令：\r\n	</p>\r\n	<div class=\"example\" id=\"example-55\">\r\n		<p>\r\n			<strong>Example #1php.ini中的 CGI 和 FastCGI 设定</strong> \r\n		</p>\r\n		<div class=\"example-contents\">\r\n			<div class=\"inicode\">\r\n<pre class=\"inicode\">fastcgi.impersonate = 1\r\nfastcgi.logging = 0\r\ncgi.fix_pathinfo=1\r\ncgi.force_redirect = 0</pre>\r\n			</div>\r\n		</div>\r\n	</div>\r\n	<p class=\"para\">\r\n		通过 IIS 管理界面或者命令行工具配置对应于 PHP 的 IIS 程序映射。\r\n	</p>\r\n	<div class=\"sect3\" id=\"install.windows.iis7.fastcgi.conf.ui\">\r\n		<h4 class=\"title\">\r\n			使用 IIS 管理界面来创建 PHP 的程序映射<a class=\"genanchor\" href=\"http://php.net/manual/zh/install.windows.iis7.php#install.windows.iis7.fastcgi.conf.ui\"> <br />\r\n</a> \r\n		</h4>\r\n		<p class=\"para\">\r\n			按照以下步骤在 IIS 管理界面中创建 PHP 的程序映射：\r\n		</p>\r\n		<div class=\"procedure\">\r\n			<ol>\r\n				<li>\r\n					<p class=\"simpara\">\r\n						在 Windows 开始菜单中选择“运行...”，输入“inetmgr”并点击“确定”；\r\n					</p>\r\n				</li>\r\n				<li>\r\n					<p class=\"simpara\">\r\n						在 IIS 管理器中左边面板“连接”下面的树状图中选择该服务器的节点；\r\n					</p>\r\n				</li>\r\n				<li>\r\n					<p class=\"para\">\r\n						在中间面板下方的“功能视图”页面打开“处理程序映射”功能；\r\n					</p>\r\n					<div class=\"mediaobject\">\r\n						<div class=\"imageobject\">\r\n							<img src=\"http://php.net/manual/zh/images/b4cf2bb34e3c20eebcf8f9e8e7949efd-iis7handlericon.png\" alt=\"创建 PHP 的程序映射：处理程序映射的位置\" height=\"515\" width=\"708\" /> \r\n						</div>\r\n					</div>\r\n				</li>\r\n				<li>\r\n					<p class=\"simpara\">\r\n						在右边“操作”面板中点击“添加模块映射...”；\r\n					</p>\r\n				</li>\r\n				<li>\r\n					<p class=\"para\">\r\n						在“添加模块映射”对话框中输入以下内容：\r\n					</p>\r\n					<ul class=\"itemizedlist\">\r\n						<li class=\"listitem\">\r\n							<span class=\"simpara\">请求路径(P)：*.php</span> \r\n						</li>\r\n						<li class=\"listitem\">\r\n							<span class=\"simpara\">模块(M)：FastCgiModule</span> \r\n						</li>\r\n						<li class=\"listitem\">\r\n							<span class=\"simpara\">可执行文件(可选)(E)：C:\\[Path to PHP installation]\\php-cgi.exe</span> \r\n						</li>\r\n						<li class=\"listitem\">\r\n							<span class=\"simpara\">名称(N)：PHP_via_FastCGI</span> \r\n						</li>\r\n					</ul>\r\n				</li>\r\n				<li>\r\n					<p class=\"simpara\">\r\n						点击“请求限制(R)...”按钮并选中“仅当请求映射至以下内容时才调用处理程序(I)：”然后选择“文件或文件夹(O)”；\r\n					</p>\r\n				</li>\r\n				<li>\r\n					<p class=\"simpara\">\r\n						在所有对话框中点击确定以保存配置。\r\n					</p>\r\n				</li>\r\n			</ol>\r\n		</div>\r\n		<div class=\"mediaobject\">\r\n			<div class=\"imageobject\">\r\n				<img src=\"http://php.net/manual/zh/images/b4cf2bb34e3c20eebcf8f9e8e7949efd-iis7handlermap.png\" alt=\"创建 PHP 的程序映射：添加程序映射\" height=\"512\" width=\"705\" /> \r\n			</div>\r\n		</div>\r\n	</div>\r\n	<div class=\"sect3\" id=\"install.windows.iis7.fastcgi.conf.cmd\">\r\n		<h4 class=\"title\">\r\n			使用命令行工具创建 PHP 的程序映射<a class=\"genanchor\" href=\"http://php.net/manual/zh/install.windows.iis7.php#install.windows.iis7.fastcgi.conf.cmd\"> ¶</a> \r\n		</h4>\r\n		<p class=\"para\">\r\n			用以下命令创建一个 IIS FastCGI 处理池以使php-cgi.exe可执行文件来处理 PHP 请求。用自己系统上指向php-cgi.exe文件的绝对路径来替代fullPath参数的值。\r\n		</p>\r\n		<div class=\"example\" id=\"example-56\">\r\n			<p>\r\n				<strong>Example #2 创建 IIS FastCGI 处理池</strong> \r\n			</p>\r\n			<div class=\"example-contents\">\r\n				<div class=\"cdata\">\r\n<pre>%windir%\\system32\\inetsrv\\appcmd set config /section:system.webServer/fastCGI ^\r\n/+[fullPath=\'c:\\PHP\\php-cgi.exe\']\r\n</pre>\r\n				</div>\r\n			</div>\r\n		</div>\r\n		<p class=\"para\">\r\n			用以下命令配置 IIS 处理针对 PHP 的请求。用自己系统上指向php-cgi.exe文件的绝对路径来替代scriptProcessor参数的值。\r\n		</p>\r\n		<div class=\"example\" id=\"example-57\">\r\n			<p>\r\n				<strong>Example #3 创建对应于 PHP 请求的处理程序映射</strong> \r\n			</p>\r\n			<div class=\"example-contents\">\r\n				<div class=\"cdata\">\r\n<pre>%windir%\\system32\\inetsrv\\appcmd set config /section:system.webServer/handlers ^\r\n/+[name=\'PHP_via_FastCGI\', path=\'*.php\',verb=\'*\',modules=\'FastCgiModule\',^\r\nscriptProcessor=\'c:\\PHP\\php-cgi.exe\',resourceType=\'Either\']\r\n</pre>\r\n				</div>\r\n			</div>\r\n		</div>\r\n		<p class=\"para\">\r\n			此命令创建了一个对于 *.php 文件后缀的处理程序映射，使得所有以 .php\r\n    结尾的 URL 都会被 FastCGI 模块处理。\r\n		</p>\r\n		<blockquote class=\"note\">\r\n			<p>\r\n				<strong class=\"note\">Note</strong>:\r\n			</p>\r\n			<p class=\"para\">\r\n				至此所需的安装和配置步骤就完成了。以下剩余的指示是可选项，但是强烈推荐以使得在\r\n      IIS 上达到最佳的 PHP 功能和性能。\r\n			</p>\r\n		</blockquote>\r\n	</div>\r\n</div>\r\n<div class=\"sect2\" id=\"install.windows.iis7.impersonation\">\r\n	<h3 class=\"title\">\r\n		角色扮演及文件系统访问<a class=\"genanchor\" href=\"http://php.net/manual/zh/install.windows.iis7.php#install.windows.iis7.impersonation\"> <br />\r\n</a> \r\n	</h3>\r\n	<p class=\"para\">\r\n		在 IIS 中使用 PHP 建议激活 FastCGI 的角色扮演功能。此功能在php.ini中由fastcgi.impersonate指令控制。激活角色扮演后，PHP\r\n   将以 IIS 所认证的用户帐号身份进行所有的文件系统操作。这将确保即使在（同一个主机）不同的\r\n   IIS 网站下使用了同一个 PHP 进程，只要每个网站使用了不同的用户帐号作为 IIS\r\n   身份认证，则这些网站的 PHP 脚本将不能访问彼此的文件。\r\n	</p>\r\n	<p class=\"para\">\r\n		例如在 IIS 7 中，默认配置下的匿名认证将使用内置的用户帐号 IUSR 作为默认身份。这意味着要使得\r\n   IIS 能够运行 PHP 脚本，至少要将这些脚本的读取权限授予 IUSR 帐号。如果 PHP\r\n   程序需要对某些文件或文件夹进行写入操作，那 IUSER 帐号也需要有相对应的写入权限。\r\n	</p>\r\n	<p class=\"para\">\r\n		在 IIS 7 中要查看哪个用户帐号被用作匿名认证的身份，使用以下命令。将其中的\r\n   \"Default Web Site\" 替换为自己使用的网站名。在输出的 XML\r\n   配置单元中查找userName属性。（注意：要以管理员身份运行此命令行）\r\n	</p>\r\n	<div class=\"example\" id=\"example-58\">\r\n		<p>\r\n			<strong>Example #4 确定用于 IIS 匿名认证的用户帐号</strong> \r\n		</p>\r\n		<div class=\"example-contents\">\r\n			<div class=\"cdata\">\r\n<pre>%windir%\\system32\\inetsrv\\appcmd.exe list config \"Default Web Site\" ^\r\n/section:anonymousAuthentication\r\n\r\n<system.webserver></system.webserver>\r\n</pre>\r\n			</div>\r\n		</div>\r\n	</div>\r\n	<blockquote class=\"note\">\r\n		<p>\r\n			<strong class=\"note\">Note</strong>:\r\n		</p>\r\n		<p class=\"para\">\r\n			如果在anonymousAuthentication单元中没有显示userName属性，或者其值为一个空字符串，那意味着应用程序池的身份被用于该网站的匿名身份。\r\n		</p>\r\n	</blockquote>\r\n	<p class=\"para\">\r\n		要修改文件及文件夹的权限，使用 Windows 资源管理器或者icacls命令行。\r\n	</p>\r\n	<div class=\"example\" id=\"example-59\">\r\n		<p>\r\n			<strong>Example #5 配置文件访问权限</strong> \r\n		</p>\r\n		<div class=\"example-contents\">\r\n			<div class=\"cdata\">\r\n<pre>icacls C:\\inetpub\\wwwroot\\upload /grant IUSR:(OI)(CI)(M)\r\n</pre>\r\n			</div>\r\n		</div>\r\n	</div>\r\n</div>\r\n<div class=\"sect2\" id=\"install.windows.iis7.defaultdoc\">\r\n	<h3 class=\"title\">\r\n		在 IIS 中把index.php设定为默认文档<a class=\"genanchor\" href=\"http://php.net/manual/zh/install.windows.iis7.php#install.windows.iis7.defaultdoc\"> <br />\r\n</a> \r\n	</h3>\r\n	<p class=\"para\">\r\n		IIS 的默认文档用于没有指定文件名的 HTTP 请求。对于 PHP 应用来说默认文档通常为index.php。要将index.php添加到 IIS 的默认文档列表中，使用此命令：\r\n	</p>\r\n	<div class=\"example\" id=\"example-60\">\r\n		<p>\r\n			<strong>Example #6 将index.php设为 IIS 的默认文档</strong> \r\n		</p>\r\n		<div class=\"example-contents\">\r\n			<div class=\"cdata\">\r\n<pre>%windir%\\system32\\inetsrv\\appcmd.exe set config ^\r\n-section:system.webServer/defaultDocument /+\"files.[value=\'index.php\']\" ^\r\n/commit:apphost\r\n</pre>\r\n			</div>\r\n		</div>\r\n	</div>\r\n</div>\r\n<div class=\"sect2\" id=\"install.windows.iis7.recycling\">\r\n	<h3 class=\"title\">\r\n		FastCGI 和 PHP 回收配置<a class=\"genanchor\" href=\"http://php.net/manual/zh/install.windows.iis7.php#install.windows.iis7.recycling\"> <br />\r\n</a> \r\n	</h3>\r\n	<p class=\"para\">\r\n		用以下命令配置 IIS FastCGI 对于 PHP 进程的回收设定。FastCGI 的设置instanceMaxRequests控制了单一的php-cgi.exe进程处理多少个请求之后会被 IIS 关闭。PHP 环境变量PHP_FCGI_MAX_REQUESTS控制了一个php-cgi.exe进程处理多少个请求之后会被自我回收。要确保 FastCGI 中InstanceMaxRequests的值小于或等于PHP_FCGI_MAX_REQUESTS的值。\r\n	</p>\r\n	<div class=\"example\" id=\"example-61\">\r\n		<p>\r\n			<strong>Example #7 配置 FastCGI 和 PHP 的回收</strong> \r\n		</p>\r\n		<div class=\"example-contents\">\r\n			<div class=\"cdata\">\r\n<pre>%windir%\\system32\\inetsrv\\appcmd.exe set config -section:system.webServer/fastCgi ^\r\n/[fullPath=\'c:\\php\\php-cgi.exe\'].instanceMaxRequests:10000\r\n\r\n%windir%\\system32\\inetsrv\\appcmd.exe set config -section:system.webServer/fastCgi ^\r\n/+\"[fullPath=\'C:\\{php_folder}\\php-cgi.exe\'].environmentVariables.^\r\n[name=\'PHP_FCGI_MAX_REQUESTS\',value=\'10000\']\"\r\n</pre>\r\n			</div>\r\n		</div>\r\n	</div>\r\n</div>\r\n<div class=\"sect2\" id=\"install.windows.iis7.timeouts\">\r\n	<h3 class=\"title\">\r\n		FastCGI 超时设定<a class=\"genanchor\" href=\"http://php.net/manual/zh/install.windows.iis7.php#install.windows.iis7.timeouts\"> <br />\r\n</a> \r\n	</h3>\r\n	<p class=\"para\">\r\n		如果会有一些需时较长的 PHP 脚本运行，则增加超时的设定值。有两个控制超时的指令activityTimeout和requestTimeout。用以下命令修改超时设定。确保在fullPath的参数中使用自己系统上的php-cgi.exe的绝对路径。\r\n	</p>\r\n	<div class=\"example\" id=\"example-62\">\r\n		<p>\r\n			<strong>Example #8 配置 FastCGI 超时设定</strong> \r\n		</p>\r\n		<div class=\"example-contents\">\r\n			<div class=\"cdata\">\r\n<pre>%windir%\\system32\\inetsrv\\appcmd.exe set config -section:system.webServer/fastCgi ^\r\n/[fullPath=\'C:\\php\\php-cgi.exe\',arguments=\'\'].activityTimeout:\"90\"  /commit:apphost\r\n\r\n%windir%\\system32\\inetsrv\\appcmd.exe set config -section:system.webServer/fastCgi ^\r\n/[fullPath=\'C:\\php\\php-cgi.exe\',arguments=\'\'].requestTimeout:\"90\"  /commit:apphost\r\n</pre>\r\n			</div>\r\n		</div>\r\n	</div>\r\n</div>\r\n<h3 class=\"title\">\r\n	改变php.ini文件的位置<a class=\"genanchor\" href=\"http://php.net/manual/zh/install.windows.iis7.php#install.windows.iis7.phpinilocation\"> <br />\r\n</a> \r\n</h3>\r\n<p class=\"para\">\r\n	PHP 在几个位置搜索<a href=\"http://php.net/manual/zh/configuration.file.php\" class=\"link\">配置文件</a> php.ini，可以通过环境变量PHPRC来改变php.ini的默认位置。要使得 PHP 从用户指定的位置加载配置文件，使用以下命令。指向php.ini文件的绝对路径应作为环境变量PHPRC的值。\r\n</p>\r\n<div class=\"example\" id=\"example-63\">\r\n	<p>\r\n		<strong>Example #9 改变php.ini文件的位置</strong> \r\n	</p>\r\n	<div class=\"example-contents\">\r\n		<div class=\"cdata\">\r\n<pre>appcmd.exe set config  -section:system.webServer/fastCgi ^\r\n/+\"[fullPath=\'C:\\php\\php.exe\',arguments=\'\'].environmentVariables.^\r\n[name=\'PHPRC\',value=\'C:\\Some\\Directory\\\']\" /commit:apphost\r\n-----------------------------------------------------------------\r\n原文地址：http://php.net/manual/zh/install.windows.iis7.php \r\n</pre>\r\n		</div>\r\n	</div>\r\n</div>', '1436401551', '3396345482', '1', '6', '1');
INSERT INTO `mr_article` VALUES ('22', '获取真实IP', '&lt;?php<br />\r\nfunction GetIP(){<br />\r\nif (getenv(\"HTTP_CLIENT_IP\") &amp;&amp; strcasecmp(getenv(\"HTTP_CLIENT_IP\"), \"未知IP\"))<br />\r\n$ip = getenv(\"HTTP_CLIENT_IP\");<br />\r\nelse if (getenv(\"HTTP_X_FORWARDED_FOR\") &amp;&amp; strcasecmp(getenv(\"HTTP_X_FORWARDED_FOR\"), \"未知IP\"))<br />\r\n$ip = getenv(\"HTTP_X_FORWARDED_FOR\");<br />\r\nelse if (getenv(\"REMOTE_ADDR\") &amp;&amp; strcasecmp(getenv(\"REMOTE_ADDR\"), \"未知IP\"))<br />\r\n$ip = getenv(\"REMOTE_ADDR\");<br />\r\nelse if (isset($_SERVER[\'REMOTE_ADDR\']) &amp;&amp; $_SERVER[\'REMOTE_ADDR\'] &amp;&amp; strcasecmp($_SERVER[\'REMOTE_ADDR\'], \"未知IP\"))<br />\r\n$ip = $_SERVER[\'REMOTE_ADDR\'];<br />\r\nelse<br />\r\n$ip = \"未知IP\";<br />\r\nreturn($ip);<br />\r\n}<br />\r\n?&gt;', '1435141075', '2946570438', '1', '19', '1');
INSERT INTO `mr_article` VALUES ('23', 'PHP与MYSQL事务处理', 'PHP与MYSQL事务处理<br />\r\n<br />\r\n/*<br />\r\nMYSQL的事务处理主要有两种方法。<br />\r\n1、用begin,rollback,commit来实现<br />\r\nbegin 开始一个事务<br />\r\nrollback 事务回滚<br />\r\ncommit 事务确认<br />\r\n2、直接用set来改变mysql的自动提交模式<br />\r\nMYSQL默认是自动提交的，也就是你提交一个QUERY，它就直接执行！我们可以通过<br />\r\nset autocommit=0 禁止自动提交<br />\r\nset autocommit=1 开启自动提交<br />\r\n来实现事务的处理。<br />\r\n当你用 set autocommit=0 的时候，你以后所有的SQL都将做为事务处理，直到你用commit确认或rollback结束。<br />\r\n注意当你结束这个事务的同时也开启了个新的事务！按第一种方法只将当前的作为一个事务！<br />\r\n个人推荐使用第一种方法！<br />\r\nMYSQL中只有INNODB和BDB类型的数据表才能支持事务处理！其他的类型是不支持的！<br />\r\n***：一般MYSQL数据库默认的引擎是MyISAM,这种引擎不支持事务！如果要让MYSQL支持事务，可以自己手动修改:<br />\r\n方法如下：1.修改c:\\appserv\\mysql\\my.ini文件，找到skip-InnoDB,在前面加上#，后保存文件。<br />\r\n2.在运行中输入：services.msc,重启mysql服务。<br />\r\n3.到phpmyadmin中，mysql-&gt;show engines;(或执行mysql-&gt;show variables like \'have_%\'; ),查看InnoDB为YES,即表示数据库支持InnoDB了。<br />\r\n也就说明支持事务transaction了。<br />\r\n4.在创建表时，就可以为Storage Engine选择InnoDB引擎了。如果是以前创建的表，可以使用mysql-&gt;alter table table_name type=InnoDB;<br />\r\n或 mysql-&gt;alter table table_name engine=InnoDB;来改变数据表的引擎以支持事务。<br />\r\n*/<br />\r\n/*************** transaction--1 ***************/<br />\r\n$conn = mysql_connect(\'localhost\',\'root\',\'root\') or die (\"数据连接错误!!!\");<br />\r\nmysql_select_db(\'test\',$conn);<br />\r\nmysql_query(\"set names \'GBK\'\"); //使用GBK中文编码;<br />\r\n//开始一个事务<br />\r\nmysql_query(\"BEGIN\"); //或者mysql_query(\"START TRANSACTION\");<br />\r\n$sql = \"INSERT INTO `user` (`id`, `username`, `sex`) VALUES (NULL, \'test1\', \'0\')\";<br />\r\n$sql2 = \"INSERT INTO `user` (`did`, `username`, `sex`) VALUES (NULL, \'test1\', \'0\')\";//这条我故意写错<br />\r\n$res = mysql_query($sql);<br />\r\n$res1 = mysql_query($sql2);<br />\r\nif($res &amp;&amp; $res1){<br />\r\nmysql_query(\"COMMIT\");<br />\r\necho \'提交成功。\';<br />\r\n}else{<br />\r\nmysql_query(\"ROLLBACK\");<br />\r\necho \'数据回滚。\';<br />\r\n}<br />\r\nmysql_query(\"END\");<br />\r\n/**************** transaction--2 *******************/<br />\r\n/*方法二*/<br />\r\nmysql_query(\"SET AUTOCOMMIT=0\"); //设置mysql不自动提交，需自行用commit语句提交<br />\r\n$sql = \"INSERT INTO `user` (`id`, `username`, `sex`) VALUES (NULL, \'test1\', \'0\')\";<br />\r\n$sql2 = \"INSERT INTO `user` (`did`, `username`, `sex`) VALUES (NULL, \'test1\', \'0\')\";//这条我故意写错<br />\r\n$res = mysql_query($sql);<br />\r\n$res1 = mysql_query($sql2);<br />\r\nif($res &amp;&amp; $res1){<br />\r\nmysql_query(\"COMMIT\");<br />\r\necho \'提交成功。\';<br />\r\n}else{<br />\r\nmysql_query(\"ROLLBACK\");<br />\r\necho \'数据回滚。\';<br />\r\n}<br />\r\nmysql_query(\"END\"); //事务处理完时别忘记mysql_query(\"SET AUTOCOMMIT=1\");自动提交<br />\r\n<br />\r\n<br />\r\n/******************对于不支持事务的MyISAM引擎数据库可以使用表锁定的方法：********************/<br />\r\n<br />\r\n//MyISAM &amp; InnoDB 都支持,<br />\r\n/*<br />\r\nLOCK TABLES可以锁定用于当前线程的表。如果表被其它线程锁定，则造成堵塞，直到可以获取所有锁定为止。<br />\r\nUNLOCK TABLES可以释放被当前线程保持的任何锁定。当线程发布另一个LOCK TABLES时，或当与服务器的连接被关闭时，所有由当前线程锁定的表被隐含地解锁。<br />\r\n*/<br />\r\n<br />\r\nmysql_query(\"LOCK TABLES `user` WRITE\");//锁住`user`表<br />\r\n$sql = \"INSERT INTO `user` (`id`, `username`, `sex`) VALUES (NULL, \'test1\', \'0\')\";<br />\r\n$res = mysql_query($sql);<br />\r\nif($res){<br />\r\necho \'提交成功。!\';<br />\r\n}else{<br />\r\necho \'失败!\';<br />\r\n}<br />\r\nmysql_query(\"UNLOCK TABLES\");//解除锁定<br />\r\nMyISAM 是MySQL中默认的存储引擎，一般来说不是有太多人关心这个东西。决定使用什么样的存储引擎是一个很tricky的事情，但是还是值我们去研究一下，这里的文章只考虑 MyISAM 和InnoDB这两个，因为这两个是最常见的。 &nbsp;<br />\r\n<br />\r\n下面先让我们回答一些问题： &nbsp;<br />\r\n◆你的数据库有外键吗？ &nbsp;<br />\r\n◆你需要事务支持吗？ &nbsp;<br />\r\n◆你需要全文索引吗？ &nbsp;<br />\r\n◆你经常使用什么样的查询模式？ &nbsp;<br />\r\n◆你的数据有多大？ &nbsp;<br />\r\n<br />\r\nmyisam只有索引缓存 &nbsp;<br />\r\n<br />\r\ninnodb不分索引文件数据文件 innodb buffer &nbsp;<br />\r\n<br />\r\nmyisam只能管理索引，在索引数据大于分配的资源时，会由操作系统来cache；数据文件依赖于操作系统的cache。innodb不管是索引还是数据，都是自己来管理 &nbsp;<br />\r\n<br />\r\n思考上面这些问题可以让你找到合适的方向，但那并不是绝对的。如果你需要事务处理或是外键，那么InnoDB 可能是比较好的方式。如果你需要全文索引，那么通常来说 MyISAM是好的选择，因为这是系统内建的，然而，我们其实并不会经常地去测试两百万行记录。所以，就算是慢一点，我们可以通过使用Sphinx从 InnoDB中获得全文索引。 &nbsp;<br />\r\n<br />\r\n数据的大小，是一个影响你选择什么样存储引擎的重要因素，大尺寸的数据集趋向于选择InnoDB方式，因为其支持事务处理和故障恢复。数据库的在小决定了 故障恢复的时间长短，InnoDB可以利用事务日志进行数据恢复，这会比较快。而MyISAM可能会需要几个小时甚至几天来干这些事，InnoDB只需要 几分钟。 &nbsp;<br />\r\n<br />\r\n您操作数据库表的习惯可能也会是一个对性能影响很大的因素。比如： COUNT() 在 MyISAM 表中会非常快，而在InnoDB 表下可能会很痛苦。而主键查询则在InnoDB下会相当相当的快，但需要小心的是如果我们的主键太长了也会导致性能问题。大批的inserts 语句在 MyISAM下会快一些，但是updates 在InnoDB 下会更快一些——尤其在并发量大的时候。 &nbsp;<br />\r\n<br />\r\n所以，到底你检使用哪一个呢？根据经验来看，如果是一些小型的应用或项目，那么MyISAM 也许会更适合。当然，在大型的环境下使用 MyISAM 也会有很大成功的时候，但却不总是这样的。如果你正在计划使用一个超大数据量的项目，而且需要事务处理或外键支持，那么你真的应该直接使用 InnoDB方式。但需要记住InnoDB 的表需要更多的内存和存储，转换100GB 的MyISAM 表到InnoDB 表可能会让你有非常坏的体验。 &nbsp;<br />\r\n<br />\r\n=========================================================== &nbsp;<br />\r\n<br />\r\nMyISAM:这个是默认类型,它是基于传统的ISAM类型,ISAM是 Indexed Sequential Access Method (有索引的顺序访问方法) 的缩写,它是存储记录和文件的标准方法.与其他存储引擎比较,MyISAM具有检查和修复表格的大多数工具. MyISAM表格可以被压缩,而且它们支持全文搜索.它们不是事务安全的,而且也不支持外键。如果事物回滚将造成不完全回滚，不具有原子性。如果执行大量 的SELECT，MyISAM是更好的选择。 &nbsp;<br />\r\n<br />\r\nInnoDB:这种类型是事务安全的.它与BDB类型具有相同的特性,它们还支持外键.InnoDB表格速度很快.具有比BDB还丰富的特性,因此如果需 要一个事务安全的存储引擎,建议使用它.如果你的数据执行大量的INSERT或UPDATE,出于性能方面的考虑，应该使用InnoDB表, &nbsp;<br />\r\n<br />\r\n对于支持事物的InnoDB类型的标，影响速度的主要原因是AUTOCOMMIT默认设置是打开的，而且程序没有显式调用BEGIN 开始事务，导致每插入一条都自动Commit，严重影响了速度。可以在执行sql前调用begin，多条sql形成一个事物（即使autocommit打 开也可以），将大大提高性能。 &nbsp;<br />\r\n<br />\r\n=============================================================== &nbsp;<br />\r\n<br />\r\nInnoDB和MyISAM是在使用MySQL最常用的两个表类型，各有优缺点，视具体应用而定。下面是已知的两者之间的差别，仅供参考。 &nbsp;<br />\r\n<br />\r\ninnodb &nbsp;<br />\r\nInnoDB 给 MySQL 提供了具有事务(commit)、回滚(rollback)和崩溃修复能力 (crash recovery capabilities)的事务安全(transaction-safe (ACID compliant))型表。 InnoDB 提供了行锁(locking on row level)，提供与 Oracle 类型一致的不加锁读取(non- locking read in SELECTs)。这些特性均提高了多用户并发操作的性能表现。在InnoDB表中不需要扩大锁定 (lock escalation)，因为 InnoDB 的列锁定(row level locks)适宜非常小的空间。 InnoDB 是 MySQL 上第一个提供外键约束(FOREIGN KEY constraints)的表引擎。 &nbsp;<br />\r\n<br />\r\nInnoDB 的设计目标是处理大容量数据库系统，它的 CPU 利用率是其它基于磁盘的关系数据库引擎所不能比的。在技术上，InnoDB 是一套放在 MySQL 后台的完整数据库系统，InnoDB 在主内存中建立其专用的缓冲池用于高速缓冲数据和索引。 InnoDB 把数据和索引存放在表空间里，可能包含多个文件，这与其它的不一样，举例来说，在 MyISAM 中，表被存放在单独的文件中。InnoDB 表的大小只受限于操作系统的文件大小，一般为 2 GB。 &nbsp;<br />\r\nInnoDB所有的表都保存在同一个数据文件 ibdata1 中（也可能是多个文件，或者是独立的表空间文件）,相对来说比较不好备份，免费的方案可以是拷贝数据文件、备份 binlog，或者用 mysqldump。 &nbsp;<br />\r\n<br />\r\n<br />\r\nMyISAM &nbsp;<br />\r\nMyISAM 是MySQL缺省存贮引擎 . &nbsp;<br />\r\n<br />\r\n每张MyISAM 表被存放在三个文件 。frm 文件存放表格定义。 数据文件是MYD (MYData) 。 索引文件是 MYI (MYIndex) 引伸。 &nbsp;<br />\r\n<br />\r\n因为MyISAM相对简单所以在效率上要优于InnoDB..小型应用使用MyISAM是不错的选择. &nbsp;<br />\r\n<br />\r\nMyISAM表是保存成文件的形式,在跨平台的数据转移中使用MyISAM存储会省去不少的麻烦 &nbsp;<br />\r\n<br />\r\n以下是一些细节和具体实现的差别： &nbsp;<br />\r\n<br />\r\n1.InnoDB不支持FULLTEXT类型的索引。 &nbsp;<br />\r\n2.InnoDB 中不保存表的具体行数，也就是说，执行select count(*) from table时，InnoDB要扫描一遍整个表来计算有多少行，但是MyISAM只要简单的读出保存好的行数即可。注意的是，当count(*)语句包含 where条件时，两种表的操作是一样的。 &nbsp;<br />\r\n3.对于AUTO_INCREMENT类型的字段，InnoDB中必须包含只有该字段的索引，但是在MyISAM表中，可以和其他字段一起建立联合索引。 &nbsp;<br />\r\n4.DELETE FROM table时，InnoDB不会重新建立表，而是一行一行的删除。 &nbsp;<br />\r\n5.LOAD TABLE FROM MASTER操作对InnoDB是不起作用的，解决方法是首先把InnoDB表改成MyISAM表，导入数据后再改成InnoDB表，但是对于使用的额外的InnoDB特性（例如外键）的表不适用。 &nbsp;<br />\r\n<br />\r\n另外，InnoDB表的行锁也不是绝对的，如果在执行一个SQL语句时MySQL不能确定要扫描的范围，InnoDB表同样会锁全表，例如 update table set num=1 where name like “%aaa%” &nbsp;<br />\r\n<br />\r\n任何一种表都不是万能的，只用恰当的针对业务类型来选择合适的表类型，才能最大的发挥MySQL的性能优势。 &nbsp;<br />\r\n<br />\r\n=============================================================== &nbsp;<br />\r\n<br />\r\n<br />\r\n以下是InnoDB和MyISAM的一些联系和区别！ &nbsp;<br />\r\n<br />\r\n1. 4.0以上mysqld都支持事务，包括非max版本。3.23的需要max版本mysqld才能支持事务。 &nbsp;<br />\r\n<br />\r\n2. 创建表时如果不指定type则默认为myisam，不支持事务。 &nbsp;<br />\r\n可以用 show create table tablename 命令看表的类型。 &nbsp;<br />\r\n<br />\r\n2.1 对不支持事务的表做start/commit操作没有任何效果，在执行commit前已经提交，测试： &nbsp;<br />\r\n执行一个msyql： &nbsp;<br />\r\nuse test; &nbsp;<br />\r\ndrop table if exists tn; &nbsp;<br />\r\ncreate table tn (a varchar(10)) type=myisam; &nbsp;<br />\r\ndrop table if exists ty; &nbsp;<br />\r\ncreate table ty (a varchar(10)) type=innodb; &nbsp;<br />\r\n<br />\r\nbegin; &nbsp;<br />\r\ninsert into tn values(‘a’); &nbsp;<br />\r\ninsert into ty values(‘a’); &nbsp;<br />\r\nselect * from tn; &nbsp;<br />\r\nselect * from ty; &nbsp;<br />\r\n都能看到一条记录 &nbsp;<br />\r\n<br />\r\n执行另一个mysql： &nbsp;<br />\r\nuse test; &nbsp;<br />\r\nselect * from tn; &nbsp;<br />\r\nselect * from ty; &nbsp;<br />\r\n只有tn能看到一条记录 &nbsp;<br />\r\n然后在另一边 &nbsp;<br />\r\ncommit; &nbsp;<br />\r\n才都能看到记录。 &nbsp;<br />\r\n<br />\r\n3. 可以执行以下命令来切换非事务表到事务（数据不会丢失），innodb表比myisam表更安全： &nbsp;<br />\r\n&nbsp;&nbsp; alter table tablename type=innodb; &nbsp;<br />\r\n<br />\r\n3.1 innodb表不能用repair table命令和myisamchk -r table_name &nbsp;<br />\r\n但可以用check table，以及mysqlcheck [OPTIONS] database [tables] &nbsp;<br />\r\n<br />\r\n============================================================== &nbsp;<br />\r\n<br />\r\nmysql中使用select for update的必须针对InnoDb，并且是在一个事务中，才能起作用。 &nbsp;<br />\r\n<br />\r\nselect的条件不一样，采用的是行级锁还是表级锁也不一样。 &nbsp;<br />\r\n转http://www.neo.com.tw/archives/900 的说明 &nbsp;<br />\r\n<br />\r\n由于InnoDB 预设是Row-Level Lock，所以只有「明确」的指定主键，MySQL 才会执行Row lock (只锁住被选取的资料例) ，否则MySQL 将会执行Table Lock (将整个资料表单给锁住)。 &nbsp;<br />\r\n<br />\r\n<br />\r\n举个例子: &nbsp;<br />\r\n<br />\r\n假设有个表单products ，里面有id 跟name 二个栏位，id 是主键。 &nbsp;<br />\r\n<br />\r\n例1: (明确指定主键，并且有此笔资料，row lock) &nbsp;<br />\r\n<br />\r\nSELECT * FROM products WHERE id=’3′ FOR UPDATE; &nbsp;<br />\r\n<br />\r\n例2: (明确指定主键，若查无此笔资料，无lock) &nbsp;<br />\r\n<br />\r\nSELECT * FROM products WHERE id=’-1′ FOR UPDATE; &nbsp;<br />\r\n<br />\r\n例2: (无主键，table lock) &nbsp;<br />\r\n<br />\r\nSELECT * FROM products WHERE name=’Mouse’ FOR UPDATE; &nbsp;<br />\r\n<br />\r\n例3: (主键不明确，table lock) &nbsp;<br />\r\n<br />\r\nSELECT * FROM products WHERE id&lt;&gt;’3′ FOR UPDATE; &nbsp;<br />\r\n<br />\r\n例4: (主键不明确，table lock) &nbsp;<br />\r\n<br />\r\nSELECT * FROM products WHERE id LIKE ’3′ FOR UPDATE; &nbsp;<br />\r\n<br />\r\n注1: &nbsp;<br />\r\nFOR UPDATE 仅适用于InnoDB，且必须在交易区块(BEGIN/COMMIT)中才能生效', '1435240822', '3396345479', '1', '36', '1');
INSERT INTO `mr_article` VALUES ('24', 'IP查询接口及其简单使用方法', '<p>\r\n	*. 网址：http://ip.taobao.com/\r\n</p>\r\n<p>\r\n	*. 请求接口（GET）：http://ip.taobao.com/service/getIpInfo.php?ip=[ip地址]\r\n</p>\r\n<p>\r\n	*.&nbsp;返回数据格式：（json格式的）国家 、省（自治区或直辖市）、市（县）、运营商。例如：\r\n</p>\r\n<pre>{\"code\":0,\"data\":{\"ip\":\"210.75.225.254\",\"country\":\"\\u4e2d\\u56fd\",\"area\":\"\\u534e\\u5317\",\r\n\"region\":\"\\u5317\\u4eac\\u5e02\",\"city\":\"\\u5317\\u4eac\\u5e02\",\"county\":\"\",\"isp\":\"\\u7535\\u4fe1\",\r\n\"country_id\":\"86\",\"area_id\":\"100000\",\"region_id\":\"110000\",\"city_id\":\"110000\",\r\n\"county_id\":\"-1\",\"isp_id\":\"100017\"}}</pre>\r\n<p>\r\n	注：其中code的值的含义为，0：成功，1：失败。\r\n</p>\r\n<p>\r\n	<strong>*. &nbsp;淘宝IP接口使用示例PHP版：</strong> \r\n</p>\r\n<pre>\r\n\r\n<pre>$ip=get_ip_data();\r\nfunction get_ip_data(){\r\n	$ip=file_get_contents(\"http://ip.taobao.com/service/getIpInfo.php?ip=\".get_client_ip());\r\n	$ip = json_decode($ip);\r\n	if($ip-&gt;code){\r\n		return false;\r\n	}\r\n	$data = (array) $ip-&gt;data;\r\n	print_r($data);//return $data;\r\n}</pre>\r\n//取客户端 ip\r\nfunction get_client_ip(){\r\n	if (isset($_SERVER[\'HTTP_CLIENT_IP\']) and !empty($_SERVER[\'HTTP_CLIENT_IP\'])){\r\n		return $_SERVER[\'HTTP_CLIENT_IP\'];\r\n	}\r\n	if (isset($_SERVER[\'HTTP_X_FORWARDED_FOR\']) and !empty($_SERVER[\'HTTP_X_FORWARDED_FOR\'])){\r\n		return strtok($_SERVER[\'HTTP_X_FORWARDED_FOR\'], \',\');\r\n	}\r\n	if (isset($_SERVER[\'HTTP_PROXY_USER\']) and !empty($_SERVER[\'HTTP_PROXY_USER\'])){\r\n		return $_SERVER[\'HTTP_PROXY_USER\'];\r\n	}\r\n	if (isset($_SERVER[\'REMOTE_ADDR\']) and !empty($_SERVER[\'REMOTE_ADDR\'])){\r\n		return $_SERVER[\'REMOTE_ADDR\'];\r\n	} else {\r\n		return \"0.0.0.0\";\r\n	}\r\n}<br />\r\n<strong></strong></pre>', '1435548758', '3396345482', '1', '25', '1');

-- ----------------------------
-- Table structure for mr_kind
-- ----------------------------
DROP TABLE IF EXISTS `mr_kind`;
CREATE TABLE `mr_kind` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '分类id',
  `name` varchar(80) NOT NULL COMMENT '分类名称',
  `location` int(10) NOT NULL COMMENT '排序',
  `status` tinyint(4) NOT NULL COMMENT '分类状态',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;

