  `land_time` int(10) NOT NULL COMMENT '登陆时间',
  `land_ip` bigint(20) NOT NULL COMMENT '登陆IP',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=38 DEFAULT CHARSET=utf8;

