-- ----------------------------
-- Table structure for mr_member
-- ----------------------------
DROP TABLE IF EXISTS `mr_member`;
CREATE TABLE `mr_member` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '用户ID',
  `username` varchar(80) NOT NULL COMMENT '登录名',
  `password` varchar(32) NOT NULL COMMENT '密码',
  `nickname` varchar(80) NOT NULL COMMENT '用户名',
  `number` int(10) NOT NULL COMMENT '登陆次数',
  `land_time` int(10) NOT NULL COMMENT '登录时间',
  `land_ip` bigint(20) NOT NULL COMMENT '登陆IP',
  `status` tinyint(4) NOT NULL COMMENT '状态',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

