-- ----------------------------
-- Table structure for mr_nav
-- ----------------------------
DROP TABLE IF EXISTS `mr_nav`;
CREATE TABLE `mr_nav` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '导航栏',
  `pid` int(10) NOT NULL COMMENT '父类',
  `name` varchar(80) NOT NULL COMMENT '导航名',
  `status` tinyint(4) NOT NULL COMMENT '状态',
  `level` tinyint(4) NOT NULL COMMENT '级别',
  `href` varchar(80) NOT NULL COMMENT '链接地址',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=13 DEFAULT CHARSET=utf8;

